CREATE TABLE cooking_logs (
    cooking_log_id INT PRIMARY KEY,
    recipe_id INT NOT NULL,
    user_id INT NOT NULL,
    actual_time TIME,
    satisfaction_rating DECIMAL(3,1),
    technique_rating TINYINT,
    complexity_rating TINYINT,
    log_summary TEXT,
    log_image_url VARCHAR(255),
    logged_at DATETIME NOT NULL,
    CONSTRAINT fk_logs_recipe FOREIGN KEY (recipe_id) REFERENCES recipes(recipe_id),
    CONSTRAINT fk_logs_user FOREIGN KEY (user_id) REFERENCES users(user_id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

