-- 產品主表
CREATE TABLE `products` (
    `product_id` INT PRIMARY KEY AUTO_INCREMENT,
    `product_name` VARCHAR(100) NOT NULL,
    `product_category` VARCHAR(20) NOT NULL,
    `product_description` VARCHAR(1000) NOT NULL,
    `product_price` DECIMAL(10,2) NOT NULL,
    `product_kcal` DECIMAL(7,2),
    `product_carbs` DECIMAL(5,2),
    `product_fat` DECIMAL(5,2),
    `product_fiber` DECIMAL(5,2),
    `product_protein` DECIMAL(5,2),
    `product_saturated_fat` DECIMAL(5,2),
    `product_sugar` DECIMAL(5,2),
    `product_sodium` DECIMAL(7,2),
    `product_net_weight` DECIMAL(10,2) NOT NULL,
    `product_ingredients` VARCHAR(1000) NOT NULL,
    `product_cooking_method` VARCHAR(1000) NOT NULL,
    `product_storage_method` VARCHAR(40) NOT NULL,
    `product_reminder` VARCHAR(100),
    `product_release` BOOLEAN NOT NULL DEFAULT TRUE
);

-- 產品圖片關聯表
CREATE TABLE `product_images` (
    `id` INT PRIMARY KEY AUTO_INCREMENT,
    `product_id` INT NOT NULL,
    `image_url` VARCHAR(255) NOT NULL,
    `sort_order` INT DEFAULT 1,
    `is_cover` BOOLEAN DEFAULT FALSE,
    FOREIGN KEY (`product_id`) REFERENCES `products`(`product_id`) ON DELETE CASCADE
);

INSERT INTO `products` (
    `product_id`, `product_name`, `product_category`, `product_description`, `product_price`, 
    `product_kcal`, `product_carbs`, `product_fat`, `product_fiber`, `product_protein`, 
    `product_saturated_fat`, `product_sugar`, `product_sodium`, `product_net_weight`, 
    `product_ingredients`, `product_cooking_method`, `product_storage_method`, `product_reminder`, `product_release`
) VALUES
(1, '舒肥雞胸藜麥飯', '低卡健身系列', '想吃得健康，又不想犧牲美味？採用低溫烹調技術鎖住肉汁...', 160.00, 250.00, 42.00, 4.20, 5.50, 28.50, 0.80, 0.50, 420.00, 250.00, '嚴選低溫舒肥嫩雞胸、黃金兩色藜麥糙米飯、五彩均衡鮮蔬', '微波加熱：2-3分鐘；隔水加熱：5-8分鐘；電鍋加熱', '冷凍 -18度 以下', '本產品含有穀類製品', 1),
(2, '香煎鮭魚糙米便當', '低卡健身系列', '煎得焦香亮澤的鮭魚排，撒上少許黑胡椒與海鹽提味，入口即化', 220.00, 580.00, 72.50, 4.20, 8.00, 35.00, 6.00, 5.00, 800.00, 480.00, '嚴選阿拉斯加鮭魚、季節時蔬與配菜、Q彈糙米飯', '微波加熱：2-3 分鐘；電鍋加熱：外鍋半杯水', '冷凍 -18度 以下', '本產品含有魚類及蛋類', 1),
(3, '蒜香毛豆嫩豬排', '低卡健身系列', '選用台灣在地活菌豬，搭配飽滿翠綠的非基改毛豆', 180.00, 318.00, 15.00, 14.20, 2.50, 32.50, 8.00, 6.00, 1000.00, 200.00, '嚴選秘製嫩豬排、清甜毛豆仁、特製蒜蓉醬包', '香煎：雙面各 1.5 分鐘；拌炒：加入醬包翻炒', '冷凍 -18度 以下', '本產品含有大豆及含麩質之穀物', 1),
(4, '豆腐漢堡排佐野菇', '低卡健身系列', '選用非基因改造黃豆製作的嫩豆腐，打造如雲朵般綿密軟嫩的漢堡排', 150.00, 232.00, 2.00, 11.60, 2.50, 18.50, 4.00, 6.00, 1000.00, 200.00, '非基因改造板豆腐、新鮮洋蔥、新鮮蕈菇類、野菇醬', '微波加熱：2-3 分鐘；推薦平底鍋煎至微焦', '冷凍 -18度 以下', '本產品含有大豆', 1),
(5, '檸檬紙包魚片', '低卡健身系列', '選用肉質細緻的頂級白肉魚片，搭配新鮮切片黃檸檬', 210.00, 215.00, 6.40, 8.20, 2.50, 28.50, 2.50, 3.00, 800.00, 150.00, '優質白肉魚片、鮮甜蘆筍、彩色甜椒、黃節瓜、特調醬包', '烤箱：200°C 烘烤 15-18 分鐘；氣炸鍋：180°C', '冷凍 -18度 以下', '生產線亦處理甲殼類、大豆、牛奶', 1),
(6, '日式壽喜燒牛丼', '日韓風味系列', '嚴選油脂分布均勻的優質牛胸腹肉，搭配鮮甜日本洋蔥', 170.00, 386.00, 15.60, 28.40, 1.20, 16.80, 15.00, 20.00, 1200.00, 200.00, '優質牛胸腹肉、新鮮洋蔥、職人秘製壽喜燒醬', '隔水加熱：5-7 分鐘；微波加熱：2-3 分鐘', '冷凍 -18度 以下', '本產品與其他含有堅果、魚類等產線共用', 1),
(7, '韓式泡菜炒豬肉', '日韓風味系列', '熟成恰到好處的發酵老泡菜，搭配油脂分佈均勻的特選豬五花', 165.00, 462.50, 4.10, 33.80, 3.00, 31.00, 11.00, 12.00, 1600.00, 250.00, '韓式發酵泡菜、軟嫩多汁豬肉、職人秘製醬汁', '隔水加熱：5-8 分鐘；平底鍋熱炒 3 分鐘', '冷凍 -18度 以下', '本產品含有大豆、小麥、魚類、蝦及芝麻', 1),
(8, '北海道咖哩雞', '日韓風味系列', '以優質牛乳與鮮奶油為基底，佐以多種辛香料低溫慢火熬煮', 155.00, 420.00, 26.00, 25.20, 2.80, 22.40, 11.00, 16.00, 1200.00, 280.00, '去骨雞腿肉、馬鈴薯、胡蘿蔔、北海道白咖哩', '隔水加熱：5-8 分鐘；微波加熱：2-3 分鐘', '冷凍 -18度 以下', '本產品含有牛奶、羊奶、含麩質之穀物', 1),
(9, '照燒鮭魚排', '日韓風味系列', '鮭魚富含 Omega-3，低溫醃製入味，煎烤後外皮微焦酥香', 230.00, 215.00, 5.60, 13.20, 0.00, 18.50, 5.00, 15.00, 1200.00, 150.00, '嚴選厚切鮭魚、職人秘製照燒醬汁', '香煎：兩面各煎 3 分鐘；氣炸鍋：180°C 10-12 分鐘', '冷凍 -18度 以下', '本產品含有魚類、大豆及含麩質之穀物', 1),
(10, '經典義大利肉醬麵', '歐美西式系列', '優質紐澳草飼牛與國產豬肉，以7:3黃金比例混合慢燉', 145.00, 290.00, 27.60, 13.80, 2.80, 8.50, 10.00, 12.00, 1300.00, 360.00, '直人秘製肉醬包、手工麵條', '水煮加熱：5 分鐘；微波加熱：2 分鐘', '冷凍 -18度 以下', '本產品含有麩質之穀物製品', 1),
(11, '奶油松露野菇燉飯', '歐美西式系列', '當義大利頂級黑松露的木質芬芳遇上濃郁溫潤的法式鮮奶油', 195.00, 495.00, 57.90, 24.60, 5.40, 10.50, 15.00, 8.00, 1200.00, 350.00, '義大利產卡納羅利米、黑松露醬、帕瑪森起司、綜合野菇', '隔水加熱：5-8 分鐘；微波加熱：3 分鐘', '冷凍 -18度 以下', '本產品含有牛奶及其製品', 1),
(12, '美式紐奧良烤雞翅', '歐美西式系列', '正宗紐奧良風味，加入數十種靈魂香料低溫長時間醃製', 160.00, 430.00, 28.40, 10.00, 0.00, 33.00, 10.00, 8.00, 1000.00, 200.00, '精選雞翅(三節翅或二節翅)、紐奧良靈魂香料', '氣炸鍋：180°C 12-15 分鐘；烤箱：200°C 15-18 分鐘', '冷凍 -18度 以下', '本產品含有大豆、含麩質之穀物', 1),
(13, '白酒蒜香蛤蜊麵', '歐美西式系列', '飽滿蛤蜊搭配特級初榨橄欖油與大量金黃蒜片，酒香優雅', 185.00, 485.00, 65.00, 12.50, 0.00, 18.20, 3.00, 3.00, 1200.00, 350.00, '飽滿蛤蜊、特級橄欖油、蒜片、白酒、杜蘭小麥麵', '平底鍋拌炒：1-2 分鐘；微波加熱：3-4 分鐘', '冷凍 -18度 以下', '本產品含有甲殼類、含麩質之穀物', 1),
(14, '經典奶油培根義大利麵', '歐美西式系列', '厚切培根搭配法國進口鮮奶油與熟成帕瑪森起司', 145.00, 245.00, 23.80, 12.50, 0.00, 9.20, 20.00, 6.00, 1400.00, 350.00, '杜蘭小麥麵、特製奶油起司醬、厚切煙燻培根', '平底鍋加熱：翻炒 2 分鐘；微波加熱：3-4 分鐘', '冷凍 -18度 以下', '本產品含有牛奶、蛋、含麩質之穀物', 1),
(15, '台式三杯雞', '台式家常系列', '遵循傳統「一杯麻油、一杯醬油、一杯米酒」的黃金比例', 165.00, 336.00, 9.20, 20.40, 0.00, 29.00, 9.00, 15.00, 1300.00, 200.00, '國產去骨雞腿肉、老薑片、麻油、九層塔', '隔水加熱：5-8 分鐘；微波加熱：3 分鐘', '冷凍 -18度 以下', '本產品含有大豆、小麥及芝麻', 1),
(16, '家傳紅燒肉', '台式家常系列', '嚴選帶皮五花豬肉，遵循古法長時慢燉，入口即化', 180.00, 585.00, 12.50, 45.00, 0.00, 32.50, 18.00, 20.00, 1500.00, 250.00, '嚴選國產豬五花肉、主廚秘製醬汁', '隔水加熱：8-10 分鐘；微波加熱：3-5 分鐘', '冷凍 -18度 以下', '本產品含有大豆、小麥', 1),
(17, '清蒸蔥油鱸魚', '台式家常系列', '外銷等級金目鱸魚，去骨去鱗處理，搭配爆香蔥油', 250.00, 469.70, 6.30, 25.20, 0.00, 54.60, 3.50, 3.00, 1000.00, 350.00, '去刺鱸魚片、特調蒸魚醬汁、香蔥風味油', '清蒸：電鍋 8-10 分鐘；最後淋上蔥油', '冷凍 -18度 以下', '本產品含有大豆、小麥', 1),
(18, '麻婆豆腐燒', '台式家常系列', '特製郫縣豆瓣醬結合大紅袍花椒，搭配滑嫩紮實的板豆腐', 130.00, 325.00, 12.00, 22.00, 0.00, 18.50, 8.00, 7.00, 1500.00, 250.00, '非基改板豆腐、國產豬後腿肉、大紅袍花椒、豆瓣醬', '隔水加熱：5-8 分鐘；微波加熱：2-3 分鐘', '冷凍 -18度 以下', '本產品含有大豆、小麥', 1),
(19, '客家小炒', '台式家常系列', '特級五花肉與香鮮魷魚，大火快炒出靈魂般的焦香味', 155.00, 525.00, 14.50, 38.00, 0.00, 31.30, 13.00, 8.00, 1500.00, 250.00, '台灣產豬五花肉、香乾魷魚、豆乾、特製客家醬油', '隔水加熱：3-5 分鐘；大火快炒 1 分鐘更佳', '冷凍 -18度 以下', '含有大豆、小麥、軟體動物類、甲殼類', 1),
(20, '藥膳麻油雞湯', '台式家常系列', '在地優質仿土雞腿肉，加入漢方藥膳慢火熬製，湯頭清甜', 190.00, 585.00, 9.50, 44.00, 0.00, 37.50, 12.00, 8.00, 1200.00, 500.00, '土雞腿肉、黑麻油、老薑、當歸、紅棗、枸杞', '隔水加熱：8-10 分鐘；電鍋加熱：外鍋半杯水', '冷凍 -18度 以下', '內含米酒與中藥材，孕婦請諮詢醫師', 1);


