-- 1. 建立資料表
CREATE TABLE IF NOT EXISTS RECIPES (
    RECIPE_ID INT NOT NULL,
    AUTHOR_ID INT NOT NULL,
    LINKED_PRODUCT_ID INT,
    PARENT_RECIPE_ID INT,
    PERSONAL_FOLDER_ID INT,
    ADAPTATION_TITLE VARCHAR(100),
    ADAPTATION_NOTE VARCHAR(255),
    RECIPE_TITLE VARCHAR(100) NOT NULL,
    RECIPE_IMAGE_URL VARCHAR(255) NOT NULL,
    RECIPE_DESCREPTION VARCHAR(255),
    RECIPE_SERVINGS INT NOT NULL,
    RECIPE_TOTAL_TIME TIME,
    RECIPE_DIFFICULTY DECIMAL(2,1) NOT NULL,
    RECIPE_LIKE_COUNT INT NOT NULL,
    RECIPE_KCAL_PER_100G DECIMAL(5,2),
    RECIPE_PROTEIN_PER_100G DECIMAL(5,2),
    RECIPE_FAT_PER_100G DECIMAL(5,2),
    RECIPE_CARBS_PER_100G DECIMAL(5,2),
    STATUS TINYINT NOT NULL COMMENT '0:公開, 1:未公開, 2:已下架',
    RECIPE_CREATED_AT DATETIME NOT NULL,
    RECIPE_LAST_UPDATED DATETIME,
    
    PRIMARY KEY (RECIPE_ID),
    
    -- 外鍵設定
    CONSTRAINT FK_RECIPE_AUTHOR FOREIGN KEY (AUTHOR_ID) REFERENCES USER(USER_ID),
    CONSTRAINT FK_RECIPE_PARENT FOREIGN KEY (PARENT_RECIPE_ID) REFERENCES RECIPES(RECIPE_ID),
    CONSTRAINT FK_RECIPE_FOLDER FOREIGN KEY (PERSONAL_FOLDER_ID) REFERENCES PERSONAL_FOLDERS(PERSONAL_FOLDER_ID)
);

-- 2. 匯入資料 (對應 JSON 格式與欄位)
INSERT INTO RECIPES (
    RECIPE_ID, AUTHOR_ID, LINKED_PRODUCT_ID, PARENT_RECIPE_ID, PERSONAL_FOLDER_ID, 
    ADAPTATION_TITLE, ADAPTATION_NOTE, RECIPE_TITLE, RECIPE_IMAGE_URL, 
    RECIPE_DESCREPTION, RECIPE_SERVINGS, RECIPE_TOTAL_TIME, RECIPE_DIFFICULTY, 
    RECIPE_LIKE_COUNT, RECIPE_KCAL_PER_100G, RECIPE_PROTEIN_PER_100G, 
    RECIPE_FAT_PER_100G, RECIPE_CARBS_PER_100G, STATUS, RECIPE_CREATED_AT, RECIPE_LAST_UPDATED
) VALUES 
(1, 1, NULL, NULL, NULL, NULL, NULL, '經典瑪格麗特披薩', 'img/recipes/1/cover.png', '義大利最經典的披薩，以新鮮羅勒、莫札瑞拉起司與番茄糊組成，完美呈現紅白綠三色。', 2, '01:30:00', 3, 0, 245.5, 10.3, 7.9, 33.1, 0, '2026-01-15 20:00:00', '2026-01-15 20:00:00'),
(2, 1, NULL, NULL, NULL, NULL, NULL, '抹茶熔岩蛋糕', 'img/recipes/2/cover.png', '口感濃郁的抹茶蛋糕，切開時有如岩漿般的抹茶巧克力流出，是抹茶控絕不能錯過的甜點。', 2, '00:40:00', 3.5, 0, 412.5, 7.9, 27.2, 34.6, 0, '2026-01-15 20:10:00', '2026-01-15 20:10:00'),
(3, 1, NULL, NULL, NULL, NULL, NULL, '四川宮保雞丁', 'img/recipes/3/cover.png', '川菜精髓之一，講究「糊辣荔枝味」，口感酸甜微麻，搭配酥脆花生與滑嫩雞丁，非常下飯。', 2, '00:35:00', 3, 0, 208.6, 15.8, 12.8, 7.4, 0, '2026-01-15 20:30:00', '2026-01-15 20:30:00'),
(4, 1, NULL, NULL, NULL, NULL, NULL, '奶油松露野菇寬麵', 'img/recipes/4/cover.png', '濃郁的鮮奶油與高品質黑松露醬交織出極致香氣，搭配嚼勁十足的寬麵與焦香野菇，是西餐廳等級的美味。', 1, '00:25:00', 2.5, 0, 215.8, 6.4, 13.2, 18.5, 0, '2026-01-15 20:35:00', '2026-01-15 20:35:00'),
(5, 1, NULL, NULL, NULL, NULL, NULL, '泰式青咖哩雞', 'img/recipes/5/cover.png', '道地的泰式經典名菜，青咖哩醬的辛辣與椰奶的香醇交織，搭配滑嫩雞腿肉與吸收醬汁精華的茄子，香氣誘人。', 3, '00:40:00', 3, 0, 165.8, 6.5, 12.8, 6.2, 0, '2026-01-15 20:40:00', '2026-01-15 20:40:00'),
(6, 1, NULL, NULL, NULL, NULL, NULL, '經典班尼迪克蛋', 'img/recipes/6/cover.png', '早午餐中的王者。酥脆的馬芬鋪上鹹香培根與完美的半熟水波蛋，最後淋上濃郁絲滑的荷蘭醬，口感極其豐富。', 2, '00:30:00', 4, 0, 285.4, 11.2, 22.5, 14.8, 0, '2026-01-15 20:50:00', '2026-01-15 20:50:00'),
(7, 1, NULL, NULL, NULL, NULL, NULL, '濃厚豚骨叉燒拉麵', 'img/recipes/7/cover.png', '道地的日式拉麵。乳白色的濃厚豚骨湯底，搭配低溫烹調的叉燒與半熟溏心蛋，每一口都能感受到滿滿的膠原蛋白與誠意。', 1, '00:20:00', 2, 0, 118.5, 5.8, 6.2, 9.4, 0, '2026-01-15 21:00:00', '2026-01-15 21:00:00'),
(8, 1, NULL, NULL, NULL, NULL, NULL, '經典自製鷹嘴豆泥', 'img/recipes/8/cover.png', '中東地區最具代表性的國民美食。以鷹嘴豆與塔希尼芝麻醬交織出濃郁風味，口感綿密滑順，不論是搭配口袋餅或是當作蔬菜沾醬都非常適合。', 2, '00:15:00', 1.5, 0, 270.6, 8, 18.5, 20.7, 0, '2026-01-15 21:10:00', '2026-01-15 21:10:00'),
(9, 1, NULL, NULL, NULL, NULL, NULL, '法式洋蔥濃湯', 'img/recipes/9/cover.png', '法式料理的經典靈魂。透過長時間慢火拌炒，將洋蔥的辛辣轉化為深邃的焦糖甜味，搭配濃郁高湯與焗烤起司麵包，暖心又奢華。', 2, '01:10:00', 3.5, 0, 85.4, 3.8, 4.2, 7.1, 0, '2026-01-15 21:15:00', '2026-01-15 21:15:00'),
(10, 1, NULL, NULL, NULL, NULL, NULL, '蒜香檸檬烤大蝦', 'img/recipes/10/cover.png', '簡單卻不失華麗的宴客料理。大虎蝦經過蒜末與黑胡椒醃漬入味，搭配奶油與檸檬片烘烤，蝦肉 Q 彈且散發濃郁的蒜香與清新檸檬香氣。', 2, '00:30:00', 2, 0, 128.5, 18.9, 5.8, 0.7, 0, '2026-01-15 21:20:00', '2026-01-15 21:20:00'),
(11, 1, 1, NULL, NULL, NULL, NULL, '舒肥雞胸藜麥飯', 'img/recipes/11/cover.png', '健身愛好者的首選！利用低溫舒肥技術保留雞胸肉的滑嫩多汁，搭配高纖維的藜麥飯與新鮮時蔬，輕盈、美味且無負擔。', 1, '01:20:00', 2.5, 0, 143.6, 12.2, 2.4, 18, 0, '2026-01-15 21:30:00', '2026-01-15 21:30:00'),
(12, 1, 2, NULL, NULL, NULL, NULL, '香煎鮭魚糙米便當', 'img/recipes/12/cover.png', '兼具高纖維與優質 Omega-3 的營養便當。利用鮭魚天然出的魚油拌炒時蔬，不僅減少額外油脂攝取，更讓蔬菜帶有海鮮的鮮甜感。', 1, '00:50:00', 2, 0, 191.2, 10.8, 6.2, 23.5, 0, '2026-01-16 09:00:00', '2026-01-16 09:00:00'),
(13, 1, 3, NULL, NULL, NULL, NULL, '蒜香毛豆嫩豬排', 'img/recipes/13/cover.png', '鹹甜適中的家常美味。利用刀背拍鬆的豬排吸飽了蒜香與味醂的滋味，搭配營養豐富的毛豆，是一道色香味俱全的下飯好菜。', 2, '00:30:00', 2, 0, 172.5, 15.2, 8.4, 9.1, 0, '2026-01-16 10:00:00', '2026-01-16 10:00:00'),
(14, 1, 4, NULL, NULL, NULL, NULL, '豆腐漢堡排佐野菇', 'img/recipes/14/cover.png', '兼顧健康與美味的漢堡排。將板豆腐與豬絞肉以黃金比例混合，肉質鮮嫩多汁且不油膩，搭配鹹甜的野菇醬汁，是一道老少咸宜的日式家常菜。', 2, '00:40:00', 2.5, 0, 128.1, 9.4, 8.6, 3.8, 0, '2026-01-16 11:00:00', '2026-01-16 11:00:00'),
(15, 1, 5, NULL, NULL, NULL, NULL, '檸檬紙包魚片', 'img/recipes/15/cover.png', '健康清爽的法式家常做法。利用烘焙紙將魚片與蔬菜密封，在烤箱中產生蒸氣循環，完美保留鯛魚的鮮美多汁與檸檬百里香的清新氣息。', 1, '00:25:00', 1.5, 0, 81.3, 12.8, 1.5, 2.1, 0, '2026-01-16 12:00:00', '2026-01-16 12:00:00'),
(16, 1, 6, NULL, NULL, NULL, NULL, '日式壽喜燒牛丼', 'img/recipes/16/cover.png', '道地的日式連鎖店風味。以比例完美的壽喜燒醬汁熬煮洋蔥與牛肉，最後淋上滑嫩的半熟蛋液，甜鹹適中的滋味與白飯是絕配。', 1, '00:20:00', 2, 0, 162.7, 8.1, 6.7, 16.2, 0, '2026-01-16 13:00:00', '2026-01-16 13:00:00'),
(17, 1, 7, NULL, NULL, NULL, NULL, '韓式泡菜炒豬肉', 'img/recipes/17/cover.png', '白飯殺手！酸辣爽口的韓式泡菜與油脂均勻的梅花豬肉片是大絕配，搭配韓式辣醬翻炒，是一道充滿韓式道地風味的熱炒料理。', 2, '00:20:00', 1.5, 0, 147.6, 8.6, 10.2, 5, 0, '2026-01-16 14:00:00', '2026-01-16 14:00:00'),
(18, 1, 8, NULL, NULL, NULL, NULL, '北海道咖哩雞', 'img/recipes/18/cover.png', '充滿北國風情的溫暖料理。在濃郁的日式咖哩中加入鮮奶油，勾勒出如白雪般的滑順奶香，搭配大塊鬆軟的根莖類蔬菜，每一口都飽含層次感。', 3, '00:50:00', 2.5, 0, 95.8, 5.9, 3.8, 7.5, 0, '2026-01-16 15:00:00', '2026-01-16 15:00:00'),
(19, 1, NULL, NULL, NULL, NULL, NULL, '味噌薑汁燒肉', 'img/recipes/19/cover.png', '日本家庭料理的經典代表！利用味噌的醇厚取代單純醬油，結合辛香薑泥與油脂豐潤的豬腹肉，鹹甜濃郁的滋味配上清脆的高麗菜絲，是極致的白飯殺手。', 2, '00:20:00', 1.5, 0, 315.4, 6.8, 30.5, 4.2, 0, '2026-01-16 16:00:00', '2026-01-16 16:00:00'),
(20, 1, 9, NULL, NULL, NULL, NULL, '照燒鮭魚排', 'img/recipes/20/cover.png', '經典日式風味。外皮酥脆的鮭魚排裹上鹹甜濃郁的自製照燒醬，亮澤的醬汁掛在魚肉上極其誘人，搭配清爽的水煮青花菜，是一道兼具營養與視覺層次的家常料理。', 1, '00:25:00', 2, 0, 177.8, 12.2, 11, 6.7, 0, '2026-01-16 17:00:00', '2026-01-16 17:00:00'),
(21, 1, 10, NULL, NULL, NULL, NULL, '經典義大利肉醬麵', 'img/recipes/21/cover.png', '最經典的義式療癒料理。以慢火熬煮的波隆那肉醬，融合番茄的微酸與牛豬肉的濃厚脂香，每一根麵條都緊緊裹滿了精華醬汁。', 2, '00:45:00', 2.5, 0, 147.2, 6.8, 5.4, 16.5, 0, '2026-01-19 19:00:00', '2026-01-19 19:00:00'),
(22, 1, 11, NULL, NULL, NULL, NULL, '奶油松露野菇燉飯', 'img/recipes/22/cover.png', '義大利料理的極致展現。選用澱粉含量高的 Arborio 米，透過不斷攪拌與分次加入高湯，釋放出米粒精華，最後拌入黑松露醬與起司，濃郁絲滑且香氣撲鼻。', 2, '00:45:00', 3.5, 0, 168.4, 4.2, 7.9, 19.5, 0, '2026-01-19 20:00:00', '2026-01-19 20:00:00'),
(23, 1, 12, NULL, NULL, NULL, NULL, '美式紐奧良烤雞翅', 'img/recipes/23/cover.png', '派對必備的經典美味！以紅椒粉與多種香料醃漬入味，透過高溫烘烤鎖住肉汁，最後刷上蜂蜜水，呈現出令人垂涎的焦糖光澤與微甜微辣的層次感。', 2, '01:30:00', 2, 0, 215.4, 16.2, 13.8, 6.5, 0, '2026-01-19 21:00:00', '2026-01-19 21:00:00'),
(24, 1, 13, NULL, NULL, NULL, NULL, '白酒蒜香蛤蜊麵', 'img/recipes/24/cover.png', '地中海風味的縮影。利用白葡萄酒激發蛤蜊的鮮甜，並透過快速拌炒讓橄欖油與蛤蜊原汁完美乳化成濃郁醬汁，包裹住每根 Q 彈的細扁麵。', 1, '00:25:00', 3, 0, 162.5, 7.8, 6.2, 18.4, 0, '2026-01-19 21:30:00', '2026-01-19 21:30:00'),
(25, 1, 14, NULL, NULL, NULL, NULL, '經典奶油培根義大利麵', 'img/recipes/25/cover.png', '羅馬經典的家常風味。不使用過多調味，僅憑蛋黃、熟成起司與培根的油脂乳化成濃厚醬汁。這道料理的靈魂在於精準的餘溫掌控，交織出絲滑且充滿黑胡椒香氣的極致口感。', 1, '00:20:00', 3.5, 0, 242.8, 10.8, 12.4, 21.6, 0, '2026-01-19 22:00:00', '2026-01-19 22:00:00'),
(26, 1, 15, NULL, NULL, NULL, NULL, '台式三杯雞', 'img/recipes/26/cover.png', '最正宗的台式熱炒風味。透過黑麻油低溫煸香老薑，再以醬油、米酒與砂糖黃金比例燉煮，最後利用餘溫逼出九層塔的強烈香氣。色澤油亮、鹹甜下飯。', 2, '00:30:00', 2.5, 0, 182.4, 14.2, 11.5, 5.4, 0, '2026-01-19 23:00:00', '2026-01-19 23:00:00'),
(27, 1, 16, NULL, NULL, NULL, NULL, '家傳紅燒肉', 'img/recipes/27/cover.png', '濃油赤醬的傳承美味。選用肥瘦相間的五花肉，透過傳統的炒糖色技術，讓肉塊呈現誘人的深琥珀色。經過長時間小火慢燉，油脂完美釋放，口感軟糯、醇厚而不膩。', 4, '01:10:00', 3, 0, 420.5, 12.8, 38.6, 6.4, 0, '2026-01-20 09:00:00', '2026-01-20 09:00:00'),
(28, 1, 17, NULL, NULL, NULL, NULL, '清蒸蔥油鱸魚', 'img/recipes/28/cover.png', '廣東餐館最常見的經典大菜。利用大火蒸氣快速鎖住鱸魚的鮮美，最後淋上燒熱的蔥油，讓鮮香層次瞬間爆發，魚肉口感如絲緞般滑嫩。', 2, '00:20:00', 2, 0, 115.4, 14.8, 5.8, 1.2, 0, '2026-01-20 10:30:00', '2026-01-20 10:30:00'),
(29, 1, 18, NULL, NULL, NULL, NULL, '麻婆豆腐燒', 'img/recipes/29/cover.png', '川菜中知名度最高的一道家常料理。透過豆瓣醬炒出的紅油賦予豆腐深邃的辣味，搭配花椒的麻與牛肉般的絞肉焦香，口感滑嫩且極度開胃下飯。', 2, '00:25:00', 3, 0, 118.5, 7.4, 8.2, 3.9, 0, '2026-01-20 14:00:00', '2026-01-20 14:00:00'),
(30, 1, 19, NULL, NULL, NULL, NULL, '客家小炒', 'img/recipes/30/cover.png', '正宗台式客家風味。這道料理的靈魂在於將五花肉與豆乾煸至焦香，搭配泡發魷魚的鮮甜與芹菜的清脆，鹹鮮帶甜且帶有強烈的鑊氣，是極致的下酒與下飯菜。', 2, '00:40:00', 3, 0, 272.4, 8.4, 20.2, 3.5, 0, '2026-01-20 15:30:00', '2026-01-20 15:30:00'),
(31, 1, 20, NULL, NULL, NULL, NULL, '藥膳麻油雞湯', 'img/recipes/31/cover.png', '暖心暖胃的台式經典補湯。選用優質黑麻油低溫煸炒老薑至乾燥收縮，激發出溫潤香氣，搭配全米酒或半水酒燉煮出的甘甜雞湯，是寒冬裡最幸福的滋味。', 3, '00:35:00', 2.5, 0, 182.4, 11.2, 12.6, 2.1, 0, '2026-01-20 19:00:00', '2026-01-20 19:00:00'),
(32, 1, NULL, NULL, NULL, NULL, NULL, '經典日式舒芙蕾鬆餅', 'img/recipes/32/cover.png', '如雲朵般蓬鬆柔軟。這道日式排隊名店的靈魂在於細膩的蛋白霜與精準的鍋內蒸烤，每一口都充滿濃郁蛋香與香草香氣，入口即化，是午後最奢華的享受。', 1, '00:30:00', 4, 0, 185.2, 7.5, 6.8, 23.4, 0, '2026-01-20 20:00:00', '2026-01-20 20:00:00'),
(33, 1, NULL, NULL, NULL, NULL, NULL, '焦糖牛奶雞蛋布丁', 'img/recipes/33/cover.png', '大人小孩都愛的療癒甜點。底層是帶著微苦香氣的深琥珀色焦糖，上層則是質地細緻、如絲綢般滑順的雞蛋布丁。透過過篩與低溫水浴烘烤，徹底消除氣泡，達到完美的入口即化感。', 4, '01:00:00', 3, 0, 158.4, 4.8, 4.5, 24.2, 0, '2026-01-20 21:00:00', '2026-01-20 21:00:00'),
(34, 1, NULL, NULL, NULL, NULL, NULL, '蜂蜜奶油厚片吐司', 'img/recipes/34/cover.png', '簡單卻不簡單的極致美味。透過井字切痕讓濃郁的無鹽奶油滲透進厚實的麵包纖維中，高溫烘烤出酥脆的金黃外皮，最後淋上絲滑蜂蜜，甜鹹交織的經典滋味令人難以抗拒。', 1, '00:10:00', 1, 0, 345.2, 5.7, 16.5, 43.4, 0, '2026-01-20 20:30:00', '2026-01-20 20:30:00'),
(35, 1, NULL, NULL, NULL, NULL, NULL, '香草生乳奶酪', 'img/recipes/35/cover.png', '純淨且濃郁的白滑享受。以 1:1 的鮮奶與鮮奶油黃金比例調製，加入香草精勾勒出高雅香氣。每一口都能感受到絲絨般的生乳質感，是甜點菜單上最經典不敗的選擇。', 4, '04:20:00', 1.5, 0, 218.7, 4.8, 16.5, 12.2, 0, '2026-01-20 21:30:00', '2026-01-20 21:30:00'),
(36, 1, NULL, NULL, NULL, NULL, NULL, '家常香蕉磅蛋糕', 'img/recipes/36/cover.png', '充滿熟成香蕉自然甜味的家常美味。透過傳統的磅蛋糕製作法（Creaming Method），將奶油打發至蓬鬆輕盈，烘烤出的蛋糕組織扎實且濕潤，是消耗過熟香蕉的最佳方案。', 6, '01:00:00', 2.5, 0, 292.5, 4.6, 14.8, 36.2, 0, '2026-01-20 20:30:00', '2026-01-20 20:30:00'),
(37, 1, NULL, NULL, NULL, NULL, NULL, '蔓越莓燕麥餅乾', 'img/recipes/37/cover.png', '充滿嚼勁與穀物香氣的健康系甜點。這款餅乾結合了燕麥的質樸口感與蔓越莓乾的酸甜，使用黑糖能讓餅乾中心保持濕潤軟嚼，外層則帶著微脆，是下午茶的最佳伴侶。', 12, '00:30:00', 2, 0, 428.5, 5.8, 18.2, 62.4, 0, '2026-01-20 21:00:00', '2026-01-20 21:00:00'),
(38, 1, NULL, NULL, NULL, NULL, NULL, '伯爵茶瑪德蓮', 'img/recipes/38/cover.png', '優雅的法式午茶經典。選用佛手柑香氣濃郁的伯爵茶葉，搭配溫潤的蜂蜜與香醇奶油。透過長時間靜置麵糊，使茶香與蛋奶完美融合，烘烤出擁有迷人金黃色澤與可愛凸肚臍的濕潤小蛋糕。', 6, '01:30:00', 3, 0, 395.4, 5.2, 24.8, 37.6, 0, '2026-01-21 14:00:00', '2026-01-21 14:00:00'),
(39, 1, NULL, NULL, NULL, NULL, NULL, '楓糖核桃司康', 'img/recipes/39/cover.png', '道地的英式酥餅風味。利用冰奶油與麵粉搓出的粗砂質地，創造出司康特有的酥鬆口感。楓糖的優雅甜味與脆口核桃完美契合，是週末早午餐或下午茶的絕佳選擇。', 4, '00:45:00', 3, 0, 420.5, 8.6, 20.9, 50.5, 0, '2026-01-21 15:30:00', '2026-01-21 15:30:00'),
(40, 1, NULL, NULL, NULL, NULL, NULL, '黑巧克力布朗尼', 'img/recipes/40/cover.png', '濃郁紮實的極致巧克力體驗。這款布朗尼擁有如生巧克力般的濕潤核心，表層帶著裂紋微脆。透過優質黑巧克力磚與無糖可可粉的雙重堆疊，呈現出微苦不膩、層次深邃的質感。', 9, '00:45:00', 2, 0, 459.7, 6.2, 32.5, 38.8, 0, '2026-01-21 21:00:00', '2026-01-21 21:00:00'),
(41, 1, NULL, NULL, NULL, NULL, NULL, '法式經典可麗露', 'img/recipes/41/cover.png', '波爾多地區的傳奇甜點。外層擁有如巧克力般深褐色的酥脆焦糖殼，內裡卻是滑順、充滿孔洞的 Q 彈組織。這道甜點極其考驗耐心與火候，必須經過 24 小時的麵糊熟成，才能讓香草與蘭姆酒的香氣達到巔峰。', 12, '25:30:00', 4.5, 0, 218.4, 4.5, 6.8, 35.2, 0, '2026-01-22 10:00:00', '2026-01-22 10:00:00'),
(42, 1, NULL, NULL, NULL, NULL, NULL, '法式經典檸檬塔', 'img/recipes/42/cover.png', '甜點店的永恆經典。完美的檸檬塔結合了充滿杏仁香氣的酥脆塔殼，以及酸爽清新、質地如絲綢般滑順的檸檬凝乳。透過精準的溫度控制與奶油乳化技術，呈現出酸甜平衡的極致美味。', 6, '02:30:00', 3.5, 0, 366.5, 5.4, 23.8, 32.2, 0, '2026-01-22 14:00:00', '2026-01-22 14:00:00'),
(43, 1, NULL, NULL, NULL, NULL, NULL, '法式經典蒙布朗', 'img/recipes/43/cover.png', '以白朗峰命名的經典法式甜點。底層是入口即化的酥脆蛋白餅，核心包裹著輕盈的鮮奶油與一整顆糖漬栗子，外層覆蓋著如山脈般的細緻栗子奶油，最後灑上如殘雪般的糖粉，口感層次分明且優雅。', 6, '02:00:00', 4, 0, 328.6, 3.8, 18.5, 36.4, 0, '2026-01-22 16:00:00', '2026-01-22 16:00:00'),
(44, 1, NULL, NULL, NULL, NULL, NULL, '日式水果三明治', 'img/recipes/44/cover.png', '風靡日本的經典甜點。選用質地柔軟、帶有奶香的生吐司，包裹著打發至結實的香甜鮮奶油與當季鮮果。透過精密的擺放設計與充分冷藏定型，切開後的橫切面如藝術品般迷人，口感輕盈不膩。', 2, '01:20:00', 2, 0, 247.8, 3.5, 16.1, 23, 0, '2026-01-22 17:00:00', '2026-01-22 17:00:00'),
(45, 1, NULL, NULL, NULL, NULL, NULL, '藍莓優格果實杯', 'img/recipes/45/cover.png', '兼具高顏值與營養的輕食選擇。使用質地濃稠、蛋白質豐富的希臘優格作為基底，搭配酥脆燕麥片與新鮮多汁的藍莓，透過層次堆疊打造出豐富的咀嚼感，是健康早餐或午後點心的完美替代方案。', 1, '00:10:00', 1, 0, 138.4, 7.2, 3.8, 19.5, 0, '2026-01-22 17:30:00', '2026-01-22 17:30:00'),
(46, 1, NULL, NULL, NULL, NULL, NULL, '黑芝麻豆乳凍', 'img/recipes/46/cover.png', '兼具健康與濃郁滋味的日式風甜點。選用高品質黑芝麻粉搭配純淨無糖豆漿，透過低溫熬煮激發出深層的穀物香氣。質地介於布丁與奶酪之間，滑順Q彈，每一口都是滿滿的芝麻韻味。', 3, '04:30:00', 1.5, 0, 124.5, 4.6, 7.2, 10.4, 0, '2026-01-22 18:00:00', '2026-01-22 18:00:00'),
(47, 1, NULL, NULL, NULL, NULL, NULL, '全麥堅果小圓餅', 'img/recipes/47/cover.png', '純植物性的健康酥餅。捨棄了奶油與精緻砂糖，改以風味純淨的橄欖油與天然楓糖調味，搭配富含口感的烘烤核桃與杏仁，每一口都能品嚐到穀物的原始清香，是追求低負擔飲食的完美點心。', 4, '00:35:00', 1.5, 0, 456.2, 10.4, 25.6, 51.5, 0, '2026-01-22 18:30:00', '2026-01-22 18:30:00'),
(48, 1, NULL, NULL, NULL, NULL, NULL, '微甜紅豆奶凍', 'img/recipes/48/cover.png', '充滿古早味的純粹滋味。手工慢火熬煮的蜜紅豆，保留了豆粒的飽滿與鬆軟質感，沉澱在絲滑濃郁的鮮奶凍底層。不需烤箱，透過簡單的冷凝法，讓奶香與豆香完美結合，是炎炎夏日或飯後最療癒的清爽點心。', 4, '13:00:00', 2.5, 0, 136.5, 4.8, 2.4, 24.2, 0, '2026-01-22 19:30:00', '2026-01-22 19:30:00'),
(49, 1, NULL, NULL, NULL, NULL, NULL, '家常韓式辣炒年糕', 'img/recipes/49/cover.png', '道地的韓國街頭國民美食。以富有嚼勁的年糕條配上鮮美的韓式魚板，裹滿甜辣適中的濃厚醬汁。透過魚高湯的底蘊與高麗菜的自然甜味，交織出層次豐富的火熱口感，是暖心又過癮的家常美味。', 2, '00:30:00', 2, 0, 182.5, 6.8, 2.5, 33.2, 0, '2026-01-22 20:00:00', '2026-01-22 20:00:00'),
(50, 1, NULL, NULL, NULL, NULL, NULL, '經典韓式部隊鍋', 'img/recipes/50/cover.png', '融合美式食材與韓式靈魂的超人氣鍋物。將濃郁的韓式甜辣湯底，與午餐肉、起司、泡麵等經典配料一同燉煮。隨著起司緩緩融化包裹住Q彈的麵條，每一口都能感受到多層次的鹹香與滿足感，是多人聚餐的首選。', 3, '00:30:00', 2, 0, 192.5, 8.4, 12.2, 13.6, 0, '2026-01-22 21:00:00', '2026-01-22 21:00:00'),
(51, 1, NULL, NULL, NULL, NULL, NULL, '靈魂泡菜豬肉豆腐鍋', 'img/recipes/51/cover.png', '這是一道充滿深度火氣的韓式靈魂料理。透過黑芝麻油低溫爆香，將熟成老泡菜的酸爽與豬五花的甘甜油脂充分融合。洗米水燉煮出的湯頭濃郁帶有層次，配上吸滿湯汁的滑嫩豆腐，每一口都是最道地的韓式風味。', 2, '00:25:00', 1.5, 0, 114.2, 6.5, 9.2, 3.5, 0, '2026-01-23 11:30:00', '2026-01-23 11:30:00'),
(52, 1, NULL, NULL, NULL, NULL, NULL, '經典石鍋拌飯', 'img/recipes/52/cover.png', '視覺與味覺的雙重饗宴。五色蔬菜鋪滿在熱騰騰的白飯上，搭配香氣撲鼻的炒牛肉與特製甜辣拌飯醬。最吸引人之處在於石鍋高溫烤出的香脆鍋巴，與所有食材拌勻後，每一口都能品嚐到多層次的豐富口感。', 1, '00:40:00', 3, 0, 135.6, 6.8, 5.2, 15.4, 0, '2026-01-23 14:00:00', '2026-01-23 14:00:00'),
(53, 1, NULL, NULL, NULL, NULL, NULL, '韓式蜂蜜炸雞', 'img/recipes/53/cover.png', '大人小孩都無法抗拒的經典韓式美味。不同於辣味炸雞，蜂蜜奶油風味強調的是蒜香與蜜香的優雅結合。透過兩次油炸技術達到外皮極致酥脆，最後裹上充滿光澤的濃厚金黃醬汁，每一口都是鹹甜交織的誘惑。', 2, '00:45:00', 3, 0, 312.4, 14.2, 18.6, 22.5, 0, '2026-01-23 15:00:00', '2026-01-23 15:00:00'),
(54, 1, NULL, NULL, NULL, NULL, NULL, '鮮菇枸杞紅棗雞湯', 'img/recipes/54/cover.png', '暖心滋補的家常好湯。選用彈牙的雞腿肉，搭配多種新鮮菇類帶出的自然鮮甜。透過乾香菇與紅棗燉煮出的深邃底蘊，最後點綴飽滿枸杞，湯頭清澈甘甜而不油膩，是四季皆宜的營養膳食。', 3, '01:00:00', 1.5, 0, 48.5, 5.2, 2.1, 1.8, 0, '2026-01-23 16:30:00', '2026-01-23 16:30:00'),
(55, 1, NULL, NULL, NULL, NULL, NULL, '家常排骨玉米蘿蔔湯', 'img/recipes/55/cover.png', '最樸實卻最雋永的台式家常湯品。透過慢火燉煮，將排骨的肉香、白蘿蔔的清甜與玉米的濃厚果糖完美融合。不需要多餘的調味料，僅靠食材間的鮮味堆疊，就能煮出一鍋清澈見底、溫潤入心的鮮美高湯。', 4, '01:00:00', 1.5, 0, 44.3, 2.8, 2.5, 3.2, 0, '2026-01-23 13:00:00', '2026-01-23 13:00:00'),
(56, 1, NULL, NULL, NULL, NULL, NULL, '經典家常酸辣湯', 'img/recipes/56/cover.png', '中式麵食館的靈魂伴侶。這道湯品講究「酸、辣、鹹、鮮」的層次平衡，透過細緻的刀工將食材化為絲滑口感。掌握先勾芡後淋蛋液的秘訣，讓湯頭濃郁透亮，最後注入大量的烏醋與白胡椒，熱氣騰騰中散發迷人的辛香與酸度。', 4, '00:30:00', 2.5, 0, 38.5, 3.2, 1.8, 2.6, 0, '2026-01-23 14:00:00', '2026-01-23 14:00:00'),
(57, 1, NULL, NULL, NULL, NULL, NULL, '剝皮辣椒蛤蜊雞湯', 'img/recipes/57/cover.png', '這是一款兼具甘、甜、辣、鮮的層次系湯品。選用醃漬入味的剝皮辣椒，其特有的微辣發酵湯底與蛤蜊的海洋鮮甜完美融合，不但能勾勒出雞腿肉的軟嫩，更是一道極佳的暖胃滋補聖品。', 3, '00:50:00', 2, 0, 52.4, 5.8, 2.2, 1.5, 0, '2026-01-23 15:30:00', '2026-01-23 15:30:00'),
(58, 1, NULL, NULL, NULL, NULL, NULL, '番茄排骨蛋花湯', 'img/recipes/58/cover.png', '色彩明亮、酸甜開胃的暖心湯品。不同於一般的蛋花湯，這道料理加入了燉至軟嫩的豬小排，增加湯頭的厚度與肉香。透過先炒後煮的技巧，讓番茄的鮮紅茄紅素與天然酸香完美融入湯中，最後以輕柔蛋花收尾，口感豐富且清爽不膩。', 4, '00:55:00', 1.5, 0, 41.8, 3, 2.9, 0.6, 0, '2026-01-23 16:00:00', '2026-01-23 16:00:00'),
(59, 1, NULL, NULL, NULL, NULL, NULL, '經典川味紅燒牛肉麵', 'img/recipes/59/cover.png', '台灣國民美食的巔峰之作。選用帶筋的牛腱肉，經過慢火燉煮後口感 Q 彈軟嫩，入口即化。湯頭以豆瓣醬爆香為基底，結合紅白蘿蔔的清甜與八角的辛香，熬出濃郁紅亮的靈魂湯底，搭配勁道的麵條，每一口都是深厚的滿足感。', 4, '02:30:00', 4, 0, 142.5, 9.2, 4.8, 15.6, 0, '2026-01-23 18:00:00', '2026-01-23 18:00:00'),
(60, 1, NULL, NULL, NULL, NULL, NULL, '經典墨西哥牛肉塔可', 'img/recipes/60/cover.png', '充滿南美熱情的街頭美食。選用香氣十足的玉米餅皮，包覆著以孜然與紅椒粉炒製的惹味牛絞肉，搭配清爽的生菜絲、多汁的番茄與鮮製莎莎醬，最後抹上酸奶油與擠上綠檸檬汁，鹹甜酸辣多重滋味在口中爆發。', 2, '00:25:00', 2, 0, 195.8, 11.2, 10.5, 14.8, 0, '2026-01-23 21:00:00', '2026-01-23 21:00:00'),
(61, 1, NULL, NULL, NULL, NULL, NULL, '泰式冬蔭功海鮮湯', 'img/recipes/61/cover.png', '泰國最具代表性的國寶級湯品。以新鮮大虎蝦頭煸炒出的蝦油為底，熬煮香茅、南薑與檸檬葉的清新辛香。結合了海鮮的鮮甜、檸檬的果酸與泰式辣椒的溫潤甜辣，最後加入椰奶勾勒出絲滑圓潤的口感，酸辣開胃，層次極其豐富。', 2, '00:40:00', 3.5, 0, 62.5, 4.8, 3.6, 2.2, 0, '2026-01-23 21:15:00', '2026-01-23 21:15:00'),
(62, 1, NULL, NULL, NULL, NULL, NULL, '經典凱薩雞肉沙拉', 'img/recipes/62/cover.png', '舉世聞名的沙拉之王。這道料理的靈魂在於手工鮮製的凱薩醬，融合了鯷魚的鹹鮮、蒜香與帕馬森起司的濃郁風味。搭配爽脆的蘿蔓生菜與香酥麵包丁，再鋪上低脂嫩煎雞胸肉，口感層次極其豐富，是輕食與前菜的不二之選。', 2, '00:30:00', 2.5, 0, 118.5, 9.4, 7.2, 4.5, 0, '2026-01-23 22:00:00', '2026-01-23 22:00:00');