CREATE TABLE steps (
    step_id INT NOT NULL,
    recipe_id INT NOT NULL,
    step_order INT,
    step_title VARCHAR(100),
    step_total_time TIME,
    step_content VARCHAR(255) NOT NULL,
    step_image_url VARCHAR(255),
    is_modified BOOLEAN NOT NULL,
    PRIMARY KEY (step_id),
    CONSTRAINT fk_steps_recipe FOREIGN KEY (recipe_id) REFERENCES recipes(recipe_id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

